import React, { useState } from "react";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import { useNavigate } from "react-router";

export default function coursecontent() {
 const [form, setForm] = useState({
   course_content: ""
 });
 const navigate = useNavigate();

 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new record to the database.
   const newPerson = { ...form };

   await fetch("http://localhost:5050/record", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newPerson),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   setForm({ name: "", position: "", level: "", created_by: "",course_content: "" });
   navigate("/");
 }

 // This following section will display the form that takes the input from the user.
 return (
  <Popup trigger={<button> Trigger</button>} posiiton = "right center">
   <div>
     <h3>Detailed course content</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="name">coursecontent</label>
         <input
           type="text"
           className="form-control"
           id="name"
           value={form.name}
           onChange={(e) => updateForm({ coursecontent: e.target.value })}
         />
       </div>
     </form>
   </div>
  </Popup>
 );
}
